const correctAnswers=["b","b","b","b"],form=document.querySelector(".question-form");form.addEventListener("submit",e=>{e.preventDefault();let t=0;[form.q1.value,form.q2.value,form.q3.value,form.q4.value].forEach((e,r)=>{e===correctAnswers[r]&&(t+=25)}),scrollTo({top:0,behavior:"smooth"});const r=document.querySelector(".result");r.style.display="block";let o=0;const l=setInterval(()=>{r.querySelector("span").textContent=`${o}%`,o===t?clearInterval(l):o++},10)});